import React, {useState,useEffect} from 'react';
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';


export default class KalenderContent extends React.Component{

    constructor() {
        super();
        this.state = {
            error:null,
            isLoaded:true,
            pruefungen: []
        };
    }

    componentDidMount() {
        fetch("http://localhost:8000/api/liste/",{
            method:'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())

        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    pruefungen: result

                });
            },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }




    render() {

        const { error, isLoaded, pruefungen} = this.state;


        if(error) {return <div>Error: {error.message}</div>}
        else if(!isLoaded) {return <div>Loading...</div>}

        else {

            var pruefungenListe = [];
            var pruefungenHeader = [];

            if(pruefungen[0]){
                var pruefungenKeys = Object.keys(pruefungen[0]);

                console.log(pruefungenKeys);
                for(let i = 0; i<pruefungen.length; i++) {
                    let pruefungenValues = Object.values(pruefungen[i]);
                    for(let j = 0; j<pruefungenValues.length; j++){
                        pruefungenListe.push(<td>{pruefungenValues[j]}</td>);
                    }
                }


                for(let i = 0; i<pruefungenKeys.length; i++){
                    pruefungenHeader.push(<td style = {{textTransform: "capitalize"}} >{pruefungenKeys[i]}</td>);
                }

            }




            /*for(let i = 0; i<pruefungen.length; i++) {
                if (i%2===1){
                    pruefungenListe.push(
                        <tr id={pruefungen[i].id} className = 'second'>
                            <td>{pruefungen[i].name}</td>
                            <td>{pruefungen[i].pruefer}</td>
                            <td>{pruefungen[i].datum}</td>
                            <td>{pruefungen[i].semester}</td>
                            <td>{pruefungen[i].dauer}</td>
                        </tr>
                    )
                } else{
                    pruefungenListe.push(
                        <tr id={pruefungen[i].id}>
                            <td>{pruefungen[i].name}</td>
                            <td>{pruefungen[i].pruefer}</td>
                            <td>{pruefungen[i].datum}</td>
                            <td>{pruefungen[i].semester}</td>
                            <td>{pruefungen[i].dauer}</td>
                        </tr>
                    )
                }
            };*/



            return(
                <>
                <div id = "ov_content">
                    <h3 id = "header_overview">Übersicht</h3>

                    <table id = "overview_table">
                        <thead>
                            <tr>
                                {pruefungenHeader}
                                {/*ToDo: Hier werden die Spaltennamen generiert*/}
                            </tr>
                        </thead>
                        <tbody>
                            {pruefungenListe}
                            {/*rows*/}
                        </tbody>

                    </table>
                </div>  
                </>
            );
        }
    }
}
