import React from 'react';
import StyleButton from './StyleButton';

export default class StylesContent extends React.Component{


    render() {
        return(
            <>
            <ul id='styleChange'>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/style_light.css';
                        this.props.updateStyleParent({stylePath});
                    }}>Light-Style</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/style_dark.css';
                        this.props.updateStyleParent({stylePath});
                    }}>Dark-Style</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/color_BO.css';
                        this.props.updateStyleParent({stylePath});
                    }}>BO-Style</button>
                </li>
            </ul>
            <ul id='colorChange'>
                <StyleButton color = "blau" updateColorParent = {this.props.updateColorParent}/>
                <StyleButton color = "violett" updateColorParent = {this.props.updateColorParent}/>
                <StyleButton color = "gruen" updateColorParent = {this.props.updateColorParent}/>
                <StyleButton color = "tuerkis" updateColorParent = {this.props.updateColorParent}/>
                <StyleButton color = "orange" updateColorParent = {this.props.updateColorParent}/>
                <StyleButton color = "rot" updateColorParent = {this.props.updateColorParent}/>
            </ul>
            </>
        );
    }
}
