import React from 'react';

export default class KalenderContent extends React.Component{


    render() {
        return(
            <ul id='colorChange'>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/style_light.css';
                        this.props.updateStylesParent({stylePath});
                    }}>Default-Style</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/style_dark.css';
                        this.props.updateStylesParent({stylePath});
                    }}>Dark-Style</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/color_BO.css';
                        this.props.updateStylesParent({stylePath});
                    }}>BO-Style</button>
                </li>
            </ul>
        );
    }
}
