import React, {useState,useEffect} from 'react';

const pruefungInstance = ({id, name, pruefer, datum, semester, dauer}) => (
    <tr id={id}>
            <td>{name}</td>
            <td>{pruefer}</td>
            <td>{datum}</td>
            <td>{semester}</td>
            <td>{dauer}</td>
        </tr>
);



export default class KalenderContent extends React.Component{

    constructor() {
        super();
        this.state = {
            error:null,
            isLoaded:true,
            pruefungen: []
        };
        this.renderPruefungen = this.renderPruefungen.bind(this);
    }

    componentDidMount() {
        fetch("http://localhost:8000/api/liste/",{
            method:'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())

        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    pruefungen: result

                });
            },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }




    render() {

        const { error, isLoaded, pruefungen} = this.state;


        if(error) {return <div>Error: {error.message}</div>}
        else if(!isLoaded) {return <div>Loading...</div>}

        else {


            var pruefungenListe = [];
            for(let i = 0; i<pruefungen.length; i++) {
                pruefungenListe.push(
                    <tr id={pruefungen[i].id}>
                        <td>{pruefungen[i].name}</td>
                        <td>{pruefungen[i].pruefer}</td>
                        <td>{pruefungen[i].datum}</td>
                        <td>{pruefungen[i].semester}</td>
                        <td>{pruefungen[i].dauer}</td>
                    </tr>
                )
            };

            /*const rows = pruefungen.map(pruefung => (
                <tr id={pruefung.id}>
                    <td>{pruefung.name}</td>
                    <td>{pruefung.pruefer}</td>
                    <td>{pruefung.datum}</td>
                    <td>{pruefung.semester}</td>
                    <td>{pruefung.dauer}</td>
                </tr>
            ));*/

            return(
                <>
                <div id = "ov_content">
                    <h3 id = "header_overview">Ãœbersicht</h3>

                    <table id = "overview_table">
                        <thead>
                            <tr>
                                {/*ToDo: Hier werden die Spaltennamen generiert*/}
                            </tr>
                        </thead>
                        <tbody>
                            {pruefungenListe}
                            {/*rows*/}
                        </tbody>

                    </table>
                </div>  
                </>
            );
        }
    }
}
