import React from 'react';

export default class KalenderContent extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            error:null,
            isLoaded:false,
            pruefungen: []
        };
    }

    componentDidMount() {
        fetch("http://localhost:8000/api/liste/")
        .then(response => response.json())
        .then(
            (result) => {
                this.setState({
                    isLoaded:true,
                    pruefungen: result.pruefungen
                });
            },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }

    render() {

        const { error, isLoaded, pruefungen} = this.state;

        if(error) {return <div>Error: {error.message}</div>}
        else if(!isLoaded) {return <div>Loading...</div>}

        else {
            return(
                <>
                <div id = "ov_content">
                    <h3 id = "header_overview">Übersicht</h3>

                    <table id = "overview_table">
                        <thead>
                            <tr>
                                {/*ToDo: Hier werden die Spaltennamen generiert*/}
                            </tr>
                        </thead>
                        <tbody>
                            {/*ToDo: Hier wird der Körper der Übersicht generiert*/}
                            {pruefungen.map(pruefung => (
                                <tr key={pruefung.id}>
                                    <td>{pruefung.name}</td>
                                </tr>
                            ))}
                        </tbody>

                    </table>
                </div>  
                </>
            )
        }
    }
}
