import React from 'react';
import Structure from './landing/Structure';
import KalenderContent from './kalender/KalenderContent';
import UebersichtContent from './uebersicht/UebersichtContent';
import Logo from './Logo';
import {BrowserRouter, Route, Routes} from 'react-router-dom';

export default class Routing extends React.Component {
    constructor() {
        super();
        this.state = JSON.parse(window.localStorage.getItem('state')) || {
            stylePath: process.env.PUBLIC_URL + '/static/css/color.css'
        }
    }

    setState(state) {
        window.localStorage.setItem('state', JSON.stringify(state));
        super.setState(state);
    }

    render() {
        return(
            <>
            <link rel="stylesheet" type="text/css" href={this.state.stylePath} />
            <BrowserRouter>
                <Routes>

                    <Route path = '/' element = {
                        <Structure 
                        filter = {false} 
                        content = {
                        <><h2 id="welcome">Willkommen beim</h2> <Logo name="bigPic"/></>
                        }/>
                    } exact/>
                    <Route path = '/kalender' element = {
                        <Structure 
                        filter = {true} 
                        content = {<KalenderContent/>}/>
                    } exact/>
                    <Route path = '/uebersicht' element = {
                        <Structure 
                        filter = {true} 
                        content = {<UebersichtContent/>}/>
                    } exact/>
                    <Route path = '/styles' element = {
                        <Structure 
                        filter = {false} 
                        content = {<StylesContent/>}/>
                    } exact/>

                </Routes>
            </BrowserRouter>
            <ul id='colorChange'>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/color_default.css';
                        this.setState({stylePath});
                    }}>Default-Style</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/color_dark.css';
                        this.setState({stylePath});
                    }}>Dark-Style</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/color_BO.css';
                        this.setState({stylePath});
                    }}>BO-Style</button>
                </li>
            </ul>
            </>
        )
    }
}
