import React from 'react';
import {prevMonth, nextMonth, renderCalendar} from '../../js/kalender';
import { updateState } from './Kalender';
import KalenderPopup from './KalenderPopup';


function ClickableEntry({value}) {
    return(
    //<div  onClick={this.togglePopup.bind(this)}>+ {value} +</div>
    <div  > {value} </div>
    );
}

export default class KalenderContent extends React.Component{


    constructor() {
        super();
        this.state = {
            error:null,
            isLoaded:true,
            pruefungen: [],
            aktiveFilter: [],
            pruefungPopup: false,
            searchItem: ''
        };
        updateState = updateState.bind(this);
    }

    togglePopup() {
        this.setState({
            showPopup: !this.state.showPopup
        });
    }

    componentDidMount() {
        fetch("http://localhost:8000/api/liste/",{
            method:'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        
        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    pruefungen: result,
                    
                });
            },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        );
    }

    render() {


        const { error, isLoaded, pruefungen} = this.state;
        
        if(error) {return <div>Error: {error.message}</div>}
        else if(!isLoaded) {return <div>Loading...</div>}

        else {

            var pruefungenHeader = [];

            if(pruefungen[0]){
                var pruefungenKeys = Object.keys(pruefungen[0]);

                for(let i = 1; i<pruefungenKeys.length; i++){
                    
                        pruefungenHeader.push(
                        <td style = {{textTransform: "capitalize"}} 
                            id = {pruefungenKeys[i]+'_col_head'}>
                                <b>{pruefungenKeys[i]}</b>
                        </td>);
                    
                                    
                }

                for(let i = 0; i<pruefungen.length; i++) {
                    var pruefungenInstance = [];
                    let pruefungenValues = Object.values(pruefungen[i]);
                    let filteredOut = false;
                    let containsSearch = false

                    

                    if (!(pruefungenValues[5] == null)) {

                        let pruefungDatum = pruefungenValues[5].split('-');
                        pruefungDatum[1] = parseInt(pruefungDatum[1]);
                        if (typeof pruefungDatum[2] === 'string') {
                            pruefungDatum[2] = parseInt(pruefungDatum[2].split(' ')[0]);
                        }
                        
                        for(let j = 1; j<pruefungenValues.length; j++){
                                                
                            if(typeof(this.state.aktiveFilter[j])!="undefined"&&this.state.aktiveFilter[j]!=pruefungenValues[j]){
                                filteredOut = true;
                            }
                            if(pruefungenValues[j]==null){
                                pruefungenValues[j]=""
                            }
                            if(pruefungenValues[j].toString().toLowerCase().match(this.state.searchItem.toLocaleLowerCase())){
                                containsSearch = true;
                            }
                        }
                        
                        if(!filteredOut&&containsSearch) {
                            var entry = document.getElementsByClassName("y"+pruefungDatum[0] + " d"+ pruefungDatum[2] + " m"+ (pruefungDatum[1]-1));
                            //console.log("y"+pruefungDatum[0] + " d"+ pruefungDatum[2] + " m"+ (pruefungDatum[1]-1));
                            if(typeof(entry[0]) != "undefined") {
                                if(!entry[0].innerText.toString().toLowerCase().trim().match(pruefungenValues[3].trim().toLocaleLowerCase())){
                                    console.log(clickableEntry(pruefungenValues[3]));
                                    entry[0].innerHTML = clickableEntry(pruefungenValues[3]);
                                }
                                
                            }
                        }

                    }

                    
                }

                

                
            }  
        }

    
        return(
            <>
            <table id = "month">
                <tbody>
                    <tr>
                        <td className="prev" onClick={()=>{prevMonth(); updateState(this.state.aktiveFilter, this.state.searchItem);}}>
                        {'\u276E'}
                        </td>
                        
                        <td id = "cur_month">
                            A
                        </td>
                        
                        <td className="next" onClick={()=>{nextMonth(); updateState(this.state.aktiveFilter, this.state.searchItem);}}>
                            {'\u276F'}
                        </td>
                    </tr>
                    <tr>
                        <td><ClickableEntry value = "test"/></td>
                        <td id = "cur_year">
                            <p>2022</p>
                        </td>
                        <td><button onClick={this.togglePopup.bind(this)}>2022</button></td>
                        
                    </tr>
                </tbody>
            </table>
            <table id = "calendar_table">
                <thead>
                    <tr>
                        <th>Mo</th>
                        <th>Di</th>
                        <th>Mi</th>
                        <th>Do</th>
                        <th>Fr</th>
                        <th>Sa</th>
                        <th>So</th>
                    </tr>
                </thead>
                <tbody id = "days">
                    {/*Hier wird der Kalender generiert*/}
                    {/*ToDo: Füllen der content divs mit Terminen*/}
                </tbody>

            </table>

            {this.state.showPopup ? 
            <KalenderPopup
                text='Close Me'
                closePopup={this.togglePopup.bind(this)}
            />
            : null
            }

            </>
        );
    }
}
