import React, {useState,useEffect} from 'react';

export default class KalenderContent extends React.Component{

    constructor() {
        super();
        this.state = {
            error:null,
            isLoaded:false,
            pruefungen: [
                {
                    "id": 1656,
                    "pnr": 3,
                    "semester": 4,
                    "name": "abc",
                    "pruefer": "Horst",
                    "datum": "2022-04-16",
                    "pruefungsform": "mündlich",
                    "dauer": 120,
                    "teilnehmerzahl": 50,
                    "pruefungsOrdnung": 19
                },
                {
                    "id": 1657,
                    "pnr": 4,
                    "semester": 2,
                    "name": "def",
                    "pruefer": "Peter",
                    "datum": "2022-04-03",
                    "pruefungsform": "test",
                    "dauer": 60,
                    "teilnehmerzahl": 12,
                    "pruefungsOrdnung": 8
                }
            ]
        };
    }

    /*componentDidMount() {
        fetch("http://localhost:8000/api/liste/",{
            method:'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())

        .then(
            (response) => {
                this.setState({
                    isLoaded:true,
                    pruefungen: response.pruefungen
                });
            },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }*/

    render() {

        const { error, isLoaded, pruefungen} = this.state;

        if(error) {return <div>Error: {error.message}</div>}
        else if(!isLoaded) {return <div>Loading...</div>}

        else {
            const rows = pruefungen.map(pruefung => (
                <tr id={pruefung.id}>
                    <td>{pruefung.name}</td>
                    <td>{pruefung.pruefer}</td>
                    <td>{pruefung.datum}</td>
                    <td>{pruefung.semester}</td>
                    <td>{pruefung.dauer}</td>
                </tr>
            ));
            return(
                <>
                <div id = "ov_content">
                    <h3 id = "header_overview">Übersicht</h3>

                    <table id = "overview_table">
                        <thead>
                            <tr>
                                {/*ToDo: Hier werden die Spaltennamen generiert*/}
                            </tr>
                        </thead>
                        <tbody>

                            {rows}
                        </tbody>

                    </table>
                </div>  
                </>
            );
        }
    }
}
