import React from 'react';

export default class KalenderContent extends React.Component{


    render() {
        return(
            <ul id='colorChange'>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/style_light.css';
                        this.props.updateStyleParent({stylePath});
                    }}>Light-Style</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/style_dark.css';
                        this.props.updateStyleParent({stylePath});
                    }}>Dark-Style</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var colorPath= process.env.PUBLIC_URL + '/static/css/color_blue.css';
                        this.props.updateColorParent({colorPath});
                    }}>Blau</button>
                </li>
                <li>
                    <button onClick={()=>{
                        var colorPath= process.env.PUBLIC_URL + '/static/css/color_purple.css';
                        this.props.updateStyleParent({colorPath});
                    }}>Violett</button>
                </li>

                <li>
                    <button onClick={()=>{
                        var stylePath= process.env.PUBLIC_URL + '/static/css/color_BO.css';
                        this.props.updateStyleParent({stylePath});
                    }}>BO-Style</button>
                </li>
            </ul>
        );
    }
}
