import React, {useState,useEffect} from 'react';

const pruefungInstance = ({id, name, pruefer, datum, semester, dauer}) => (
    <tr id={id}>
            <td>{name}</td>
            <td>{pruefer}</td>
            <td>{datum}</td>
            <td>{semester}</td>
            <td>{dauer}</td>
        </tr>
);

function renderPruefungen() {
    const pruefungenListe = []
    for (let i = 0; i< this.state.pruefungen.length; i++) {
        let id = this.state.pruefungen[i].id;
        let name = this.state.pruefungen[i].name;
        let pruefer = this.state.pruefungen[i].pruefer;
        let datum = this.state.pruefungen[i].datum;
        let semester = this.state.pruefungen[i].semester;
        let dauer = this.state.pruefungen[i].dauer;
        pruefungenListe.push(<pruefungInstance 
            id = {id} name={name} pruefer={pruefer} datum={datum} semester={semester} dauer={dauer}/>);
    }
    return pruefungenListe;
};  

export default class KalenderContent extends React.Component{

    constructor() {
        super();
        this.state = {
            error:null,
            isLoaded:true,
            pruefungen: []
        };
        this.renderPruefungen = this.renderPruefungen.bind(this);
    }

    componentDidMount() {
        fetch("http://localhost:8000/api/liste/",{
            method:'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())

        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    pruefungen: result

                });
            },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }




    render() {

        const { error, isLoaded, pruefungen} = this.state;


        if(error) {return <div>Error: {error.message}</div>}
        else if(!isLoaded) {return <div>Loading...</div>}

